var newMsg;

var now = new Date();
var start = new Date(now.getFullYear(), 0, 0);
var diff = now - start;
var oneDay = 1000 * 60 * 60 * 24;
var day = Math.floor(diff / oneDay)
var data = {"dayx":day};
data["hour"]=now.getHours() + now.getMinutes()/60 + now.getSeconds()/3600;

var i,j;
for (i=1;i<msg.payload.length;i++){
    if (msg.payload[i-1]["Tag"]<=day && msg.payload[i]["Tag"]>day){
        var aTags = Object.keys(msg.payload[i]);
        var aValS = Object.values(msg.payload[i-1]);
        var aValE = Object.values(msg.payload[i]);
        var dayFactor = (day - msg.payload[i-1]["Tag"]) / (msg.payload[i]["Tag"] - msg.payload[i-1]["Tag"]);
        data["dayFactor"] = dayFactor;
        data["dayS"] = msg.payload[i-1];
        data["dayE"] = msg.payload[i];
        data["dayLen"] = Object.keys(msg.payload[i]).length;
        for (j=0;j<aTags.length;j++){
            if (aTags[j] != "Tag"){
                data[aTags[j]] = aValS[j] + (dayFactor * (aValE[j] - aValS[j]));
            }
        }
    }
}

newMsg = {topic:'aktDay',payload:data};

return newMsg;

var sollTemp;
if (msg.payload.hour < (msg.payload.LichtEin - 3)  || msg.payload.hour > msg.payload.LichtAus){
    sollTemp = msg.payload.TempMax -0.5;
}else{
    sollTemp = msg.payload.TempMax;
}
var newMsg = { topic:"SetTemp", payload: "Set;SollTemp;"+sollTemp };
return newMsg;

var sValue = "WriteValue:0";
//Sync Uhrzeit
var now = new Date();
var seconds = (now.getHours()*60 + now.getMinutes())*60 + now.getSeconds();
sValue += ";Sekunden:" + seconds; 

//Licht werte
sValue += ";Licht_WertEin:" + msg.payload.LichtEin; 
sValue += ";Licht_WertAus:" + msg.payload.LichtAus; 

//Futter werte
// zähle Füterungen
var futterCount = 1;
if (msg.payload.dayS.Futter1 > 0){ futterCount++; }
if (msg.payload.dayS.Futter2 > 0){ futterCount++; }
if (msg.payload.dayS.Futter3 > 0){ futterCount++; }
if (msg.payload.dayS.Futter4 > 0){ futterCount++; }
var futterStep = (msg.payload.LichtAus - msg.payload.LichtEin) / futterCount;
var futterAct = msg.payload.LichtEin + futterStep;

if (msg.payload.dayS.Futter1 > 0){
    var f1Start = Number.parseFloat(futterAct).toFixed(2);
    var f1Len = msg.payload.dayS.Futter1;
    futterAct += futterStep;
} else {
    var f1Start = 0;
    var f1Len = 0;
}    
if (msg.payload.dayS.Futter2 > 0){
    var f2Start = Number.parseFloat(futterAct).toFixed(2);
    var f2Len = msg.payload.dayS.Futter2;
    futterAct += futterStep;
} else {
    var f2Start = 0;
    var f2Len = 0;
}    
if (msg.payload.dayS.Futter3 > 0){
    var f3Start = Number.parseFloat(futterAct).toFixed(2);
    var f3Len = msg.payload.dayS.Futter3;
    futterAct += futterStep;
} else {
    var f3Start = 0;
    var f3Len = 0;
}    
if (msg.payload.dayS.Futter4 > 0){
    var f4Start = Number.parseFloat(futterAct).toFixed(2);
    var f4Len = msg.payload.dayS.Futter4;
    futterAct += futterStep;
} else {
    var f4Start = 0;
    var f4Len = 0;
}    

sValue += ";Futter1_WertStart:" + f1Start; 
sValue += ";Futter1_Sekunden:" + f1Len; 
sValue += ";Futter2_WertStart:" + f2Start; 
sValue += ";Futter2_Sekunden:" + f2Len; 
sValue += ";Futter3_WertStart:" + f3Start; 
sValue += ";Futter3_Sekunden:" + f3Len; 
sValue += ";Futter4_WertStart:" + f4Start; 
sValue += ";Futter4_Sekunden:" + f4Len; 

var newMsg = { topic:"SetFutter", payload: sValue };
return newMsg;
