var now = new Date();
var start = new Date(now.getFullYear(), 0, 0);
var diff = now - start;
var oneDay = 1000 * 60 * 60 * 24;
var day = Math.floor(diff / oneDay)
var data = {"dayx":day};
data["hour"]=now.getHours() + now.getMinutes()/60 + now.getSeconds()/3600;

var i,j;
for (i=1;i<msg.payload.length;i++){
    if (msg.payload[i-1]["Tag"]<day && msg.payload[i]["Tag"]>day){
        var aTags = Object.keys(msg.payload[i]);
        var aValS = Object.values(msg.payload[i-1]);
        var aValE = Object.values(msg.payload[i]);
        var dayFactor = (day - msg.payload[i-1]["Tag"]) / (msg.payload[i]["Tag"] - msg.payload[i-1]["Tag"]);
        data["dayFactor"] = dayFactor;
        data["dayS"] = msg.payload[i-1];
        data["dayE"] = msg.payload[i];
        data["dayLen"] = Object.keys(msg.payload[i]).length;
        for (j=0;j<aTags.length;j++){
            if (aTags[j] != "Tag"){
                data[aTags[j]] = aValS[j] + (dayFactor * (aValE[j] - aValS[j]));
            }
        }
    }
}

newMsg = {topic:'aktDay',payload:data};

return newMsg;

