var msgWatchdog = null;
var msgTelegram = null;
var msgTrigger = null;
switch(msg.topic){
    case "syncRequest":
        //-----------------------
        // Request verarbeiten
        //-----------------------
        //Ermittle Partner-ID
        flow.set('PartnerID', msg.from);

        //Reply Telegramm erzeugen
        msgTelegram = {"payload":
            {
                "nodeId":global.get('MeshID'),
                "dest":msg.from,
                "from":global.get('MeshID'),
                "type":6,
                "subs":[]
            }
        };

        //Watchdog starten
        msgWatchdog = {"payload":1};

        //Mesh-Status aktuallisieren
        global.set('MeshState', "Online");
        break;

    case "sycnReply":
        //-----------------------
        // Reply speichern
        //-----------------------
        //Topologie speichern
        var topology = {"nodeId":msg.from,
            "root":true,
            "subs":msg.subs};
        global.set('MeshTopology', topology);

        //Watchdog starten
        msgWatchdog = {"payload":1};

        //Mesh-Status aktuallisieren
        global.set('MeshState', "Online");
        break;

    case "watchdog":
        //-----------------------
        // Watchdog verarbeiten
        //-----------------------
        switch(msg.payload){
            case "Warning":
                //Mesh-Status aktuallisieren
                global.set('MeshState', "Bad");
                break;

            case "Off":
                //Mesh-Status aktuallisieren
                global.set('MeshState', "Offline");
                msgTrigger = {"payload":1};
                break;
        }
        break;

    case "trigger":
        //-----------------------
        // Request Telegram erstellen
        //-----------------------
        //-----------------------
        // Trigger verarbeiten
        //-----------------------
        switch(msg.payload){
            case "Warning":
            case "Off":
                if (global.get('MeshState') == "Offline"){
                    // Trigger neu anstossen
                    msgWatchdog = {"payload":1};
                    //Request senden
                    msgTelegram = {"payload":
                        {
                            "nodeId":global.get('MeshID'),
                            "dest":flow.get('PartnerID'),
                            "from":global.get('MeshID'),
                            "type":5,
                            "subs":[]
                        }
                };

                }
                break;
        }
}

return [msgWatchdog,msgTelegram,msgTrigger];

var msgWatchdog = null;
var msgTelegram = null;
var msgDebug = null;
var data;
var ota;
switch(msg.topic){
    case "newFile":
        //-----------------------
        // Dateiinformationen einlesen
        //-----------------------

        // Blockanzahl ermitteln
        var fullBlocks = Math.floor(msg.req.files[0].size / 1024);
        var numOfBlocks = fullBlocks;
        if (fullBlocks * 1024 < msg.req.files[0].size){
            numOfBlocks++;
        }

        // Force-Anforderung prÃ¼fen
        var forced;
        if ("force" in msg.payload){
            forced = msg.payload.force;
        }else{
            forced = false;
        }

        // OTA Daten zusammen stellen
        ota = {
            "role":msg.payload.role,
            "hardware":msg.payload.hardware,
            "md5":msg.req.files[0].bufferMd5,
            "forced":forced,
            "noPart":numOfBlocks,
            "file":msg.req.files[0].buffer
        };
        flow.set("otaAnnounce",ota);

        msgDebug = msg;
        msgDebug["ota"] = ota;

        //Announce Telegramm erzeugen
        msgTelegram = {"payload":
            {
                "dest":0,
                "from":global.get('MeshID'),
                "type":10,
                "role":ota.role,
                "hardware":ota.hardware,
                "md5":ota.md5,
                "forced":ota.forced,
                "noPart":ota.noPart
            }
        };

        //Watchdog starten
        msgWatchdog = {"payload":1};

        //OTA-Status aktuallisieren
        global.set('OtaState', "Send");
        break;

    case "newRequest":
        //-----------------------
        // OTA-File einlesen und Daten senden
        //-----------------------
        ota = flow.get("otaAnnounce");
        var rawBuffer = ota.file.slice(msg.payload.partNo * 1024,(1+msg.payload.partNo)*1024);
        var strBuffer = rawBuffer.toString("base64");

        //Data Telegramm erzeugen
        msgTelegram = {"payload":
            {
                "dest":msg.payload.from,
                "from":global.get('MeshID'),
                "type": 12,
                "role":ota.role,
                "hardware":ota.hardware,
                "md5":ota.md5,
                "forced":ota.forced,
                "noPart":ota.noPart,
                "partNo":msg.payload.partNo,
                "data":strBuffer
            }
        };

        //Watchdog starten
        msgWatchdog = {"payload":1};

        //OTA-Status aktuallisieren
        global.set('OtaState', "Activ");
        break;

    case "watchdog":
        //-----------------------
        // Watchdog verarbeiten
        //-----------------------
        switch(msg.payload){
            case "Warning":
                //OTA-Status aktuallisieren
                global.set('OtaState', "Inactiv");
                break;

            case "Off":
                //OTA-Status aktuallisieren
                global.set('OtaState', "Ready");
                break;
        }
}

return [msgTelegram,msgDebug,msgWatchdog];

